function getDistance(e, a, t, s) {
    const {sin: o, cos: c, asin: n, PI: r, hypot: i} = Math;
    let b = (e, a) => (e *= r / 180, {x: c(a *= r / 180) * c(e), y: c(a) * o(e), z: o(a)}), p = b(e, a), l = b(t, s),
        k = 2 * n(i(p.x - l.x, p.y - l.y, p.z - l.z) / 2) * 6371;
    return Math.round(k)
}

